package com.app.blogapp.model;

import com.app.blogapp.ObjectIdDeserializer;
import com.app.blogapp.ObjectIdSerializer;
import com.fasterxml.jackson.annotation.JsonProperty;
import com.fasterxml.jackson.databind.annotation.JsonDeserialize;
import com.fasterxml.jackson.databind.annotation.JsonSerialize;
import org.bson.types.ObjectId;
import org.springframework.data.annotation.Id;
import org.springframework.data.mongodb.core.mapping.Document;
import org.springframework.data.mongodb.core.mapping.Field;


@Document(collection = "Blog")
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               
public class Blog {

    @JsonProperty("_id")
    @JsonSerialize(using = ObjectIdSerializer.class)
    @Id
    private ObjectId _id;

    private String blogTitle;

    private String blogAuthor;

    private String blogContent;
    private int upVotes;
    private int downVotes;

    public Blog(ObjectId _id, String blogTitle, String blogAuthor, String blogContent, int upVotes, int downVotes) {
        this._id = _id;
        this.blogTitle = blogTitle;
        this.blogAuthor = blogAuthor;
        this.blogContent = blogContent;
        this.upVotes = upVotes;
        this.downVotes = downVotes;
    }


    public ObjectId get_id() {
        return _id;
    }

    public void set_id(ObjectId _id) {
        this._id = _id;
    }

    public String getBlogTitle() {
        return blogTitle;
    }

    public void setBlogTitle(String blogTitle) {
        this.blogTitle = blogTitle;
    }

    public String getBlogAuthor() {
        return blogAuthor;
    }

    public void setBlogAuthor(String blogAuthor) {
        this.blogAuthor = blogAuthor;
    }

    public String getBlogContent() {
        return blogContent;
    }

    public void setBlogContent(String blogContent) {
        this.blogContent = blogContent;
    }

    public int getUpVotes() {
        return upVotes;
    }

    public void setUpVotes(int upVotes) {
        this.upVotes = upVotes;
    }

    public int getDownVotes() {
        return downVotes;
    }

    public void setDownVotes(int downVotes) {
        this.downVotes = downVotes;
    }

    @Override
    public String toString() {
        return "Blog{" +
                "_id=" + _id +
                ", blogTitle='" + blogTitle + '\'' +
                ", blogAuthor='" + blogAuthor + '\'' +
                ", blogContent='" + blogContent + '\'' +
                ", upVotes=" + upVotes +
                ", downVotes=" + downVotes +
                '}';
    }
}
